	public static JSONObject XMLtoJSON(String pUrl, Map<String,String> pParameters) {
		JSONObject result = null;
		result = XML.toJSONObject(XML.toJSONObject(urlReader(pUrl,pParameters),true).getJSONObject("string").getString("content"),true);
		return result;
	}

	public static JSONObject getJSON(String pUrl, Map<String,String> pParameters){
		JSONObject result = null;
		String strResult = urlReader(pUrl,pParameters);
		result = new JSONObject(strResult.substring(1,strResult.length()-2));
		return result;
	}

	private static String urlReader(String pUrl, Map<String,String> pParameters){
		String result = null;
		String preUrl = pUrl+"?";
		for(Map.Entry<String,String> pair : pParameters.entrySet()){
			preUrl = preUrl+pair.getKey()+"="+pair.getValue()+"&";
		}
		preUrl = preUrl.substring(0,preUrl.length()-1);
		try {
			HttpURLConnection conn = (HttpURLConnection) new URL(preUrl).openConnection();
			conn.setRequestMethod("GET");
			BufferedReader rd = new BufferedReader(new InputStreamReader(conn.getInputStream(),"UTF-8"));
			result = rd.lines().collect(Collectors.joining());
			rd.close();
		} catch (MalformedURLException e) {
			e.printStackTrace();
		} catch (IOException e) {
			e.printStackTrace();
		}
		return result;
	}

